import React, { useRef, useState } from 'react'

export default function Inbox(props) {
  const today = new Date();
  const numberOfDaysToAdd = 3;
  const date = today.setDate(today.getDate() + numberOfDaysToAdd);
  const defaultValue = new Date(date).toISOString().split('T')[0]


  const [newTask, setNewTask] = useState(false)
  const titleRef = useRef()
  const calenderRef = useRef()
  const newtaskHandler = () => {
    setNewTask(true)
  }
  const addHandler = (e) => {
    e.preventDefault();
    if (titleRef.current.value === "") {
      window.alert("Task can not be empty");
      return;
    }
    const newObj = {
      number: props.list.length + 1,
      title: titleRef.current.value,
      date: new Date(calenderRef.current.value)
    }
    props.append(newObj)
    setNewTask(false)
  }
  const cancelHandler = () => {
    setNewTask(false)
  }
  return (
    <div>
      <h3>INBOX</h3>
      {!newTask && (
        <button className='new btn btn-primary' onClick={newtaskHandler}>+Add New Todo</button>
      )}
      {newTask && (
        <form className='newtask-box'>
          <input type="text" ref={titleRef} autoFocus="on"/>
          <div className="button">
            <button className='new' onClick={addHandler}>Add Task</button>
            <button className='new' onClick={cancelHandler}>Cancle</button>

            <input type="date" ref={calenderRef} defaultValue={defaultValue} />
          </div>
        </form>
      )}
      <div>
        {props.list.map((list) => {
          return (
            <div className='box' key={list.number}>
              {list.title} ({list.date.toLocaleDateString()})
            </div>
          )
        })}
      </div>
    </div>
  )
}
